import httpx
import json

def fetch_and_save_json(url, file_path):
    headers = {
        "Accept": "application/json",
        "Authorization": "Bearer AAAAAAAAAAAAAAAAAAAAANRILgAAAAAAnNwIzUejRCOuH5E6I8xnZz4puTs%3D1Zv7ttfk8LF81IUq16cHjhLTvJu4FA33AGWWjCpTnA",  # Замените на ваш токен
        "Content-Type": "application/json",
        "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:130.0) Gecko/20100101 Firefox/130.0"
    }

    with httpx.Client() as client:
        response = client.get(url, headers=headers)
        response.raise_for_status()  # Проверяет, что запрос был успешным
        data = response.json()  # Парсит JSON-ответ
        print(data)  # Выводит JSON-ответ

        # Сохраняет JSON в файл
        with open(file_path, 'w', encoding='utf-8') as f:
            json.dump(data, f, ensure_ascii=False, indent=4)
        print(f"JSON сохранен в файл: {file_path}")

